UPDATE `quest_template` SET `OfferRewardText`='These will do nicely, $N. Thank you. Just look at them... so many of them all huddled up. I almost feel bad for the Crusade, but then again, it was never their mill to take, now, was it?',`OfferRewardEmote1`=1,`OfferRewardEmote2`=6,`DetailsEmote1`=4,`DetailsEmote2`=0 WHERE `entry`=5903;
