UPDATE `quest_template` SET `OfferRewardText`='You have done it, $n.  You have saved Colonel Jules\' soul and cast the darkness within him back to where it came.  A great, noble deed this is!  A hero\'s day!',`OfferRewardEmote1`=2,`OfferRewardEmote2`=4,`RequestItemsText`='Go to Anchorite Barada, $n.  He must succeed with his ritual if the colonel is to be saved.',`DetailsEmote1`=4 WHERE `entry`=10935;
