UPDATE `quest_template` SET `OfferRewardText`='You have fought hard to invade the realm of the harvester... Wait.$B$B<Gothik looks at his notes.>$B$BMy apologies, $r. Old habits die hard.$B$BValanar sent you, then? I trust you are somewhat competent.$B$BI do have a gift for the Scarlet dogs. One that is sure to speed up their work in the mines.',`OfferRewardEmote1`=15,`OfferRewardEmote2`=6,`OfferRewardEmote3`=274,`OfferRewardEmote4`=1,`DetailsEmote1`=4 WHERE `entry`=12697;
