UPDATE `quest_template` SET `OfferRewardText`='<Orbaz reads the message.>$B$BThe armies of Hearthglen and Tirisfal will be marching into a massacre.$B$B<Orbaz smiles.>$B$BNow for the second part of the plan. You\'re going to deliver this message right into the hands of High General Abbendis. That\'s what the clothes are for...',`OfferRewardEmote1`=1,`OfferRewardEmote2`=11,`OfferRewardEmote3`=1,`RequestItemsText`='You\'re going to miss the courier and then I\'m going to be forced to kill you. By forced I mean I will take great pleasure in ending your life.$B$BHURRY UP!',`CompleteEmote`=5,`DetailsEmote1`=4 WHERE `entry`=12754;
