REPLACE INTO `npc_text` (`ID`, `text0_0`, `text0_1`, `lang0`, `prob0`, `em0_1`, `em0_2`, `em0_3`, `em0_4`, `em0_5`) VALUES (12744,'My S.T.O.U.T. is more than just a target for throwing steins, it\'s a valuable training asset.  If you can hit S.T.O.U.T. you can probably hit anything!$b$bJust try not to hit me.','','7','1','0','0','0','0','0');
